$(document).ready(function() {

    $('form.ajax').on('submit', function(e){

        var that = $(this),
            url = that.attr('action'),
            type = that.attr('method'),
            data = [];

        that.find('[name]').each( function(index, value) {

            var that = $(this),
                name = that.attr('name'),
                value = that.val();

            data[name] = value;
        });

        console.log(data);

        $.ajax({
            url: url,
            type: type,
            data: { name_this : data},
            success: function(response){
                console.log(response);
            }
        });

        e.preventDefault();

    });
});
